function calc_sum_wast(params) {
    const expectedParams = ["r1", "r2", "r3", "r4", "r5", "r6", "r7", "r8"];
    var sum = 0;
    for (key of expectedParams) {
        const val = parseInt(params.extra[key]);
        if (isNaN(val)) {
            return -1;
        }
        sum = sum + val
    }
    return sum;
}


exports.calc_sum_wast = calc_sum_wast;