function getContextsIncludingString(params, str) {
    const contexts = params.contexts;
    return contexts.filter(elem => elem.name.includes(str));
}


function getContextsWithName(params, name) {
    const contexts = params.contexts;
    return contexts.filter(elem => elem.name === name);
}


function doesContextExist(params, context) {
    const nContexts = getContextsWithName(params, context).length
    return nContexts >= 1
}


exports.getContextsIncludingString = getContextsIncludingString;
exports.getContextsWithName = getContextsWithName;
exports.doesContextExist = doesContextExist;